package Programacion1;

public class PruebatoUpperCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String cadenaUno= "javascript";
		String cadenaDos= "";
		System.out.println("Pasamos a mayuscula toda la cadena");
		cadenaDos=cadenaUno.toUpperCase();
		System.out.println("Y la nueva cadena es:" + cadenaDos);
	}

}
